var format= require('./../util/format'),
	fs=require('fs'),
	path=require('path')

module.exports={
	command:'--delete-node <componente>',
	description: 'Elimina el componente RaptorNode del directorio src',
	placeholder:'deleteNode',
	action:function(R,value,arg){
		R.lookModules();
		
		if(R.bundles[value] && fs.existsSync(path.join(R.basePath,'src',R.bundles[value].path))){
			fs.rmdirSync(path.join(R.basePath,'src',R.bundles[value].path));
			console.log(format.getFormated("El componente "+value+" fue eliminado",'blue'))
		}else{
			console.log(format.getFormated("El componente "+value+" no fue encontrado en el proyecto",'red'))
		}
	}
}