"use strict"

class ViewFunctions {

	constructor(app){
		this.R=app;
	}

	plugin(name){
		if(this.R.viewHotSpot[name]){
			var text='';
			for (var i = 0; i < this.R.viewHotSpot[name].length; i++) {
				text+=this.R.viewHotSpot[name][i];
			};
			return text;
		}
		return '';
	}

	hola(){
		return "hola mundo 3"
	}
}

module.exports=ViewFunctions;